    }

    render() {
        return (
            <div>
                <h2>Contador</h2>
                <h4>Passo: {this.state.passo}</h4>
                <h4>Valor: {this.state.valor}</h4>

                <div>
                    <button onClick={inc()}>+</button>
                    <button onClick={dec()}>-</button>
                </div>
            </div>
        )
    }
}
