import React from 'react'

export default (props) => {

    function mudaPasso(passo){
        props.mudaPasso(passo)
    }

    return (
        <div>
            <label for="passoInput">Passo: </label>
            <input id="passoInput" type="number"
                value={props.passo}
                onChange={e => mudaPasso(+e.target.value)}/>
        </div>
    )

}
