import React, { useState } from 'react'
import Sub from './Sub.jsx'

export default (props) => {

    const [num, setNum] = useState(0)

    function quandoClicar(valorGerado) {
        setNum(valorGerado)
    }

    return (
        <div>
            <h4>Valor {num}</h4>
            <Sub onClicar={quandoClicar}></Sub>
        </div>
    );
};
